import './Home.css';
import logo from './../img/logo.svg';
function Home() {
    const [isDimmed, setIsDimmed] = useState(false);
    const [selected, setSelected] = useState('');

    const handleClick = (type) => {
        setIsDimmed(true);
        setSelected(type);
    };

    return (
        <>
            {isDimmed && <div className="dimmer" />}
            <div className="content">
                <div className="topbar">
                    <p className = "auroraTopbar">Aurora</p>
                    <p classname = "about"></p>
                    <p classname = "workshops"></p>
                    <p classname = "faq"></p>
                </div>
                <div className="logoDiv">
                    <img src={logo} className="logo" alt="logo" />
                    <p className ="text">A girls-focused learnathon</p>
                    <p>Location | San Francisco, CA</p>
                </div>
                <div className="pastEvents">

                </div>
                <div className="workshops">

                </div>
                <div className="faq">
                <QuestionAnswer question ="What's a hackathon?" answer ="A social learning event"></QuestionAnswer>
                </div>
            </div>

        </>
    );
}

export default Home;
